package ternaryoperator;

public class ternary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i =2;
		int value;
		int x = 10;
		int y = 30;
		
		System.out.println("i am going to learn about of ternary");
		value = (x != y) ? 100 : 50;
		System.out.println(value);
		value = (x > y) ? 100 : 50;
		System.out.println(value);
		

	}

}
